import React, { Component } from 'react';
import {Link, withRouter} from 'react-router-dom';
import {Bar} from 'react-chartjs-2';
import axios from 'axios';

class MostAffected extends Component
{
    constructor(props) {
        super(props);
        this.state = {
            Data: {}
        }
    }

    componentDidMount() {
        axios.get(`http://localhost:3001/gettop10`)
            .then(res => {
                const fire = res.data;
                let playername = [];
                let playerscore = [];
                football.forEach(element => {
                    playername.push(element.name);
                    playerscore.push(element.score);
                });
                this.setState({
                    Data: {
                        labels: playername,
                        datasets:[
                            {
                                label:'Champions League 2017/2018 Top Scorer',
                                data: playerscore ,
                                backgroundColor:[
                                    'rgba(255,105,145,0.6)',
                                    'rgba(155,100,210,0.6)',
                                    'rgba(90,178,255,0.6)',
                                    'rgba(240,134,67,0.6)',
                                    'rgba(120,120,120,0.6)',
                                    'rgba(250,55,197,0.6)'
                                ]
                            }
                        ]
                    }
                });
            })
    }
    render()
    {
        return(
            <div>

                <ul>
                    <li><Link to="/" className="active"  >Home</Link></li>
                    <li><Link to="/fire" >Fire-Events</Link></li>
                    <li><Link >Contact</Link></li>
                    <li className="heading">DISASTER MANAGEMENT PORTAL</li>
                </ul>
                <br />
                <h2 style={{ color: '#8424c9'}}><b>TOP 10 AFFECTED REGIONS</b></h2>
                <br />
                <Bar
                    data = {this.state.Data}
                    options = {{ maintainAspectRatio: false }} />
            </div>
        )
    }
}
export default withRouter(MostAffected)
