import React, { Component } from 'react';
import {Link, withRouter} from 'react-router-dom';
import {Bar} from 'react-chartjs-2';
import axios from 'axios';
import AnimatedNumber from 'react-animated-number';
import Odometer from 'react-odometerjs';
import 'odometer/themes/odometer-theme-default.css';

import facebook from './../photos/facebook.png';
import twitter from './../photos/twitter.jpg';
import helpline from './../photos/helpline.jpg'
const config = require('./config.json');


class MostAffected extends Component
{

    constructor(props) {
        super(props);
        this.state = {
            Data: {},
            Data1: {},
            firedatas: [],
            stuck: 0,
            rescued: 0
        }
    }

    componentDidMount() {

        this.timer = setInterval(()=> this.setfirecount(), 2000);
        this.timer1 = setInterval(()=> this.setfiredata(), 2000);
        this.timer2 = setInterval(()=> this.setallcount(), 2000);
    }

    componentWillUnmount() {
        this.timer  = null;
        clearInterval(this.timer);
        this.timer1 = null;
        clearInterval(this.timer1);
        this.timer2 = null;
        clearInterval(this.timer2);
    }

    setfirecount(){
        const req_header = {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                credentials: 'include'
            }
        };
        axios.get(config.url+'getfirecount', req_header)
            .then(res => {
                const fire = res.data.results[0].data.sort(function(a, b){return b.stuck-a.stuck}).slice(0,10);
                console.log(fire);
                let city = [];
                let stuck = [];
                let rescued = [];
                fire.forEach(element => {
                    city.push(element.city);
                    stuck.push(element.stuck);
                    rescued.push(element.rescued);
                });
                this.setState({
                    Data: {
                        labels: city,
                        datasets:[
                            {
                                label:'TOP CITIES WITH FIRE CASES REGISTERED',
                                data: stuck ,
                                backgroundColor:[
                                    'rgba(255,30,30,0.8)',
                                    'rgba(155,100,210,0.8)',
                                    'rgba(90,178,255,0.8)',
                                    'rgba(240,134,67,0.8)',
                                    'rgba(120,120,120,0.8)',
                                    'rgba(250,55,197,0.8)',
                                    'rgba(148,244,129,0.8)',
                                    'rgba(244,228,128,0.8)',
                                    'rgba(247,171,133,0.8)',
                                    'rgba(255,91,91,0.8)',
                                    'rgba(91,153,255,0.8)'
                                ]
                            }
                        ]
                    }
                });

                this.setState({
                    Data1: {
                        labels: city,
                        datasets:[
                            {
                                label:'CORRESPONDINING CITIES WITH NO OF RESOLVED CASES',
                                data: rescued ,
                                backgroundColor:[
                                    'rgba(255,30,30,0.8)',
                                    'rgba(155,100,210,0.8)',
                                    'rgba(90,178,255,0.8)',
                                    'rgba(240,134,67,0.8)',
                                    'rgba(120,120,120,0.8)',
                                    'rgba(250,55,197,0.8)',
                                    'rgba(148,244,129,0.8)',
                                    'rgba(244,228,128,0.8)',
                                    'rgba(247,171,133,0.8)',
                                    'rgba(255,91,91,0.8)',
                                    'rgba(91,153,255,0.8)'
                                ]
                            }
                        ]
                    }
                });
            })
    }

    setfiredata(){
        const req_header = {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                credentials: 'include'
            }
        };
        axios.get(config.url+'getfiredata', req_header)
            .then(res => {
                this.setState({
                    firedatas: res.data.results
                })
            })
    }

    setallcount(){
        const req_header = {
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
                credentials: 'include'
            }
        };
        axios.get(config.url+'getallcount', req_header)
            .then(res => {
                console.log(res.data.results[0])
                const n1 = res.data.results[0].FireStuck;
                const n2 = res.data.results[0].FireRescued;

                this.setState({
                    stuck: n1,
                    rescued: n2
                })
            })
    }



    renderData(){
        return this.state.firedatas.map((firedata) => {
                if(firedata.source == "twitter") {
                    return (
                        <div>
                            <div className="card-body" style={{
                                width: "92%",
                                margin: "4%",
                                backgroundColor: "white",
                                borderRadius: "5%",
                                paddingBottom: "2%"
                            }} key={firedata.id}>
                                <img className="card-body" src={twitter} style={{width: "100%", height: "100px",float: "left", marginBottom: "2%", borderRadius: "5% 5% 0 0"}}key={firedata.id}/>
                                <h5 className="card-title" style={{width: "100%", textAlign: "left", marginLeft: "2%", marginTop: "2%"}} key={firedata.id}>Location: {firedata.location} </h5>
                                <p className="card-text" style={{width: "100%", textAlign: "left", marginLeft: "2%"}} key={firedata.id}>Message: {firedata.message}</p>
                            </div>
                        </div>
                    )
                }
                else if(firedata.source == "fb"){
                    return (
                        <div>
                            <div className="card-body" style={{
                                width: "92%",
                                margin: "4%",
                                backgroundColor: "white",
                                borderRadius: "5%",
                                paddingBottom: "2%"
                            }} key={firedata.id}>
                                <img className="card-body" src={facebook} style={{width: "100%", height: "100px",float: "left", marginBottom: "2%", borderRadius: "5% 5% 0 0"}} key={firedata.id}/>
                                <h5 className="card-title" style={{width: "100%", textAlign: "left", marginLeft: "2%", marginTop: "2%" }} key={firedata.id}>Location: {firedata.location} </h5>
                                <p className="card-text" style={{width: "100%", textAlign: "left", marginLeft: "2%"}} key={firedata.id}>Message: {firedata.message}</p>
                            </div>
                        </div>
                    )
                }
                else{
                    return (
                        <div key={firedata.id}>
                            <div className="card-body" style={{
                                width: "92%",
                                margin: "4%",
                                backgroundColor: "white",
                                borderRadius: "5%",
                                paddingBottom: "2%"
                            }} key={firedata.id}>
                                <img className="card-body" src={helpline} style={{width: "100%", height: "100px",float: "left", marginBottom: "2%", borderRadius: "5% 5% 0 0"}} key={firedata.id}/>
                                <h5 className="card-title" style={{width: "100%", textAlign: "left", marginLeft: "2%", marginTop: "2%" }} key={firedata.id}>Location: {firedata.location} </h5>
                                <p className="card-text" style={{width: "100%", textAlign: "left", marginLeft: "2%" }} key={firedata.id}>Message: {firedata.message}</p>
                            </div>
                        </div>
                    )
                }

            }
        )
    }



    render()
    {
        return(
            <div>

                <ul>
                    <li><Link to="/" className="active"  >Home</Link></li>
                    <li><Link to="/fire" >Fire-Events</Link></li>
                    <li><Link to="">Contact</Link></li>
                    <li className="heading">DISASTER MANAGEMENT PORTAL</li>
